import React,{ Component } from 'react';
import TicketListTable from 'components/TicketListTable';
import Table from '@material-ui/core/Table';
import { post } from 'axios';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import { CircularProgress, Box, Container } from '@material-ui/core';
import InputBase from '@material-ui/core/InputBase';
import RefundListTable from '../components/RefundListTable';
import RefundInputForm from '../components/RefundInputForm';
import SeatViewTable from 'components/SeatViewTable';
import $ from 'jquery';
// import jQuery from "jquery";

// window.$ = window.jQuery = jQuery;
  // $(document).ready(function(){
  //   show_time_val = $('select.show_time').attr('data-type');
  //   $('select.show_time option[value=' + show_time + ']').attr('selected', 'selected');
  // });

  
window.$ = $;
// 테이블의 Row 클릭시 값 가져오기
$("#ticket-list tr").click(function(){ 	

  var str = ""
  var tdArr = new Array();	// 배열 선언
  
  // 현재 클릭된 Row(<tr>)
  var tr = $(this);
  var td = tr.children();
  
  // tr.text()는 클릭된 Row 즉 tr에 있는 모든 값을 가져온다.
  console.log("클릭한 Row의 모든 데이터 : "+tr.text());
  
  // 반복문을 이용해서 배열에 값을 담아 사용할 수 도 있다.
  td.each(function(i){
    tdArr.push(td.eq(i).text());
  });
  
  console.log("배열에 담긴 값 : "+tdArr);
  
  // td.eq(index)를 통해 값을 가져올 수도 있다.
  var clicked_ticket_id = td.eq(2).text();
  
  
  str +=	"예매번호 : "+ clicked_ticket_id;		
  
  $("#All").html(" * 클릭한 Row의 모든 데이터 = " + tr.text());		
  $("#only_Ticket_id").html(str);
});
const options =[ "시간대를 선택해주세요", "10:00~12:00", "13:00~15:00", "16:00~18:00", "19:00~21:00"]
class TicketManagement extends Component{
        constructor(props) {
        super(props);
        this.state = {
          ticketings:'',
          completed:0,
          searchKeyword1:'',
          searchKeyword2:'',
          value: '시간대 선택해주세요',
          key: '',
          show: '',
          seatNo: '',
          row: '',
          column: '',
          ticket_id: ''
        }
        this.stateRefresh = this.stateRefresh.bind(this);
        this.handleFormSubmit1 =this.handleFormSubmit1.bind(this);
        this.handleFormSubmit2 =this.handleFormSubmit2.bind(this);
      }
      onChange(e) {
        this.setState({
            value: e.target.value
        })
    }
      stateRefresh = () => {
        this.setState({
          ticketings:'',
          refunds: '',
          completed:0,
          searchKeyword1:'',
          searchKeyword2:'',
          
      });
        this.callApi1()
        .then(res => this.setState({ticketings: res}))
        .catch(err => console.log(err));
        this.callApi2()
        .then(res => this.setState({refunds: res}))
        .catch(err => console.log(err));
      }

    
      componentDidMount(){
        this.timer = setInterval(this.progress, 20);
        this.callApi1()
        .then(res => this.setState({ticketings: res}))
        .catch(err => console.log(err));
        this.callApi2()
        .then(res => this.setState({refunds: res}))
        .catch(err => console.log(err));
      }
    
      callApi1 = async() => {
        const response = await fetch('/api/ticketings');
        const body = await response.json();
        return body;
      }
      callApi2 = async() => {
        const response = await fetch('/api/refunds');
        const body = await response.json();
        return body;
      }
    
      progress = () => {
        const {completed} = this.state;
        this.setState({completed: completed >= 100 ? 0 : completed +1});
      };
      handleFormSubmit1(e) {
        e.preventDefault()
        this.changeTicket_Seat()
        .then((response) => {
          console.log(response.data);
          this.props.stateRefresh();
        })
        this.setState({
          seatNo: '',
          row: '',
          column: '',
          ticket_id: ''
        })
      }
      handleFormSubmit2(e) {
        e.preventDefault()
        this.changeTicket_All()
        .then((response) => {
          console.log(response.data);
          this.props.stateRefresh();
        })
        this.setState({
          show: '',
          seatNo: '',
          row: '',
          column: '',
          ticket_id: ''
        })
      }
      selectTableRow = (e) =>{
        let t_id = {}
        t_id[e.target.ticketing_id] = e.target.value;
        this.setState(t_id);
      }
    
      handleValueChange =(e) =>{
        let nextState = {}
        nextState[e.target.name] = e.target.value;
        this.setState(nextState);
      }
      refundCreate = (data3) => {
        console.log(data3);
      }
      changeTicket_Seat(){
        const url = '/ticketing/changeSeat'
        const formData = new FormData();
        formData.append('keys', this.state.seatNo);
        formData.append('row', this.state.column);
        formData.append('column', this.state.column);
        formData.append('id', this.state.ticket_id);
        const config ={
          headers: {
            'content-type': 'multipart/form-data'
          }
        }
        return post(url, formData, config)
      }
      changeTicket_All(){
        const url = '/ticketing/changeAll'
        const formData = new FormData();
        formData.append('show', this.state.show);
        formData.append('keys', this.state.seatNo);
        formData.append('row', this.state.column);
        formData.append('column', this.state.column);
        formData.append('id', this.state.ticket_id);
        const config ={
          headers: {
            'content-type': 'multipart/form-data'
          }
        }
        return post(url, formData, config)
      }
    
      render(){
        
        
        const filteredComponents1 = (data1) =>{
           data1 = data1.filter((c) => {
             return c.phone.indexOf(this.state.searchKeyword1) > -1;
            });
          return data1.map((c) => {
            return <TicketListTable stateRefresh={this.stateRefresh} name={c.name} user_id={c.user_id} ticketing_id = {c.ticketing_id} phone={c.phone} show_title={c.show_title} show_time={c.show_time} keys={c.key} ticketing_date={c.ticketing_date}/>
          })
        }
        const cellList = ["이름", "ID", "예매 번호", "전화번호", "상영작", "시간대", "좌석번호", "예매일자"]
        
        const filteredComponents2 = (data2) =>{
          data2 = data2.filter((d) => {
            return d.phone.indexOf(this.state.searchKeyword2) > -1;
          });
         return data2.map((d) => {
           return <RefundListTable stateRefresh={this.stateRefresh} user_id={d.user_id} phone={d.phone} name={d.name} show_title={d.show_title} show_time={d.show_time} ticketing_id={d.ticketing_id} payment_type={d.payment_type} reason_type={d.reason_type}/>
         })
       }
       const cellList1 = ["ID", "이름", "전화번호", "상영작", "시간대", "예매번호", "환불유형", "사유"]

        return (
          <div>
            <Box class="ticket_list_table">
            <label class=""></label>
            <h1>예매내역</h1>
            <label>전화번호 - </label>
            <InputBase
              placeholder="검색하기"
              
              name="searchKeyword1"
              value={this.state.searchKeyword1}
              onChange = {this.handleValueChange}
              inputProps={{ 'aria-label': 'search' }}
            />
            <table id="ticket-list">
            <thead>
              <tr>
                {cellList.map(c => {
                  return <td>{c}</td>
                })}
              </tr>
            </thead>
            <tbody>
              {this.state.ticketings ? filteredComponents1(this.state.ticketings) :
              <tr >
                <td colSpan="7" align ="center" value>
                  <CircularProgress  variant="determinate" value={this.state.completed}/>
                </td>
              </tr>
      }
            </tbody>
          </table>
          <div class="col-lg-12" id="All" ></div> 
		      <div class="col-lg-12" id="only_Ticket_id" ></div> 
          </Box>
          <br/>
          <Box class="ticket_change_box">
          <a><strong>예매 변경</strong></a>
          <br/>
          <label class="change_type">변경 유형 : </label>
          <select id="change_type_box">
            <option value={this.state.value} onChange>좌석 변경</option>
            <option value="change_time">시간대 변경</option>
          </select>


          <label class="time_select"> 시간대 선택 : </label>
          <select name="show_time" class="show_time" value={this.state.value} onChange={this.onChange.bind(this)}>
           {options.map(option => {
               return <option value={option} key={option}>{option}</option>
           })}
          </select>
          
          
          <form name="input" method="post" action="">
          <label class="change_seat_number"> 예매번호 : </label>
          <input type="text" class="change_seat_number_box" name="memberName" maxlength="10" value={this.state.t_id}/>
          <label class="change_seat_number"> 변경 좌석번호 : </label>
          <input type="text" class="change_seat_number_box" name="memberName" maxlength="5" value={this.state.seatNo}/>
          <input type="hidden" class="change_seat_number_box" name="memberName" maxlength="5" value={this.state.row}/>
          <input type="hidden" class="change_seat_number_box" name="memberName" maxlength="5" value={this.state.column}/>
          <button onClick={
              this.handleFormSubmit1
              }>변경</button>
          <input type="reset" value="취소"></input>
          </form>
          <br/>
          <SeatViewTable/>
          <br/>
          </Box>
          <br/>
          <RefundInputForm
            onCreate={this.refundCreate}/>
          <br/>
          <Box class="ticket_list_table">
          <h2>환불내역</h2>
          <label>전화번호 - </label>
          <InputBase
              placeholder="검색하기"
              
              name="searchKeyword2"
              value={this.state.searchKeyword2}
              onChange = {this.handleValueChange}
              inputProps={{ 'aria-label': 'search' }}
            />
            <table>
            <thead>
              <tr>
                {cellList1.map(d => {
                  return <td>{d}</td>
                })}
              </tr>
            </thead>
            <tbody>
              {this.state.refunds ? filteredComponents2(this.state.refunds) :
              <tr>
                <td colSpan="7" align ="center">
                  <CircularProgress  variant="determinate" value={this.state.completed}/>
                </td>
              </tr>
      }
            </tbody>
          </table>
          </Box>
          <br/>
          </div>
    );
};
}

export default TicketManagement;
