import React from 'react';
class SeatViewTable extends React.Component{
    
    createSeat = () => {
        let table = []
        let c = this.props.entire_column;
        let r = this.props.entire_row;
        console.log(c);
        for (let i = 0; i < r; i++) {
            let children = []
            for (let j = 0; j < c; j++) {
                children.push(<td>{`Column ${j + 1}`}</td>)
                }
                table.push(<tr>{children}</tr>)
          }
          return table
        
        }
    
    render(){
        return(
            <div>
                <table>
                    {this.createSeat()}
                </table>
            </div>
        )
    }
}
export default SeatViewTable;
