import React from 'react';
class SeatViewTable extends React.Component{
    constructor(props){
        this.state={
            entire_row: '',
            entire_column: ''
        }
        this.stateRefresh = this.stateRefresh.bind(this);
    }
    stateRefresh = () => {
        this.setState({
            entire_row:'',
            entire_column:''
        });
        this.callApi3()
        .then(res => this.setState({seats: res}))
        .catch(err => console.log(err));
    }
    componentDidMount(){
        this.timer = setInterval(this.progress, 20);
        this.callApi3()
        .then(res => this.setState({seats: res}))
        .catch(err => console.log(err));
      }
    callApi3 = async() => {
        const response = await fetch('/ticketing/seats');
        const body = await response.json();
        return body;
      }
    createSeat = () => {
        let table = []

        for (let i = 0; i < entire_row; i++) {
            let children = []
            //Inner loop to create children
            for (let j = 0; j < entire_column; j++) {
              children.push(<td>{`Column ${j + 1}`}</td>)
            }
            //Create the parent and add the children
            table.push(<tr>{children}</tr>)
          }
          return table
        
        }
    
    render(){
        return(
            <div>
                <table>
                    {this.createSeat()}
                </table>
            </div>
        )
    }
}
export default SeatViewTable;
