import React from 'react'; 
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
window.$ = $;
// 테이블의 Row 클릭시 값 가져오기
$("#ticket-list TableRow").click(function(){ 	

  var str = ""
  var tdArr = new Array();	// 배열 선언
  
  // 현재 클릭된 Row(<TableRow>)
  var TableRow = $(this);
  var TableCell = TableRow.children();
  
  // TableRow.text()는 클릭된 Row 즉 tr에 있는 모든 값을 가져온다.
  console.log("클릭한 Row의 모든 데이터 : "+TableRow.text());
  
  // 반복문을 이용해서 배열에 값을 담아 사용할 수 도 있다.
  TableCell.each(function(i){
    tdArr.push(TableCell.eq(i).text());
  });
  
  console.log("배열에 담긴 값 : "+tdArr);
  
  // TableCell.eq(index)를 통해 값을 가져올 수도 있다.
  var clicked_ticket_id = TableCell.eq(2).text();
  
  
  str +=	"예매번호 : "+ clicked_ticket_id;		
  
  $("#All").html(" * 클릭한 Row의 모든 데이터 = " + TableRow.text());		
  $("#only_Ticket_id").html(str);
});

class TicketListTable extends React.Component{
    render(){
        return(
            <TableRow >
                <TableCell>{this.props.name}</TableCell>
                <TableCell>{this.props.user_id}</TableCell>
                <TableCell>{this.props.ticketing_id}</TableCell>
                <TableCell>{this.props.phone}</TableCell>
                <TableCell>{this.props.show_title}</TableCell>
                <TableCell>{this.props.show_time}</TableCell>
                <TableCell>{this.props.keys}</TableCell>
                <TableCell>{this.props.ticketing_date}</TableCell>
            </TableRow>
        )
    }
}
export default TicketListTable;
